defmodule Day1.First do
  def move(""), do: 0
  def move("(" <> rest), do: 1 + move(rest)
  def move(")" <> rest), do: -1 + move(rest)
end

defmodule Day1.Second do
  def first_entry_to_basement(input) do
    to_char_list(input)
    |> Enum.scan(0, fn elem, acc -> acc + direction(elem) end)
    |> Enum.find_index(fn elem -> elem == -1 end)
    |> + 1
  end

  def direction(elem) do
    case elem do
      ?( -> 1
      ?) -> -1
    end
  end
end

